import React, { useState,useRef, useEffect } from 'react';
import './App.css';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [darkTheme, setDarkTheme] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const buttonRef = useRef(null);
  const [index,setIndex] = useState(-1);

  useEffect(() => {
    const localData = localStorage.getItem('myData');
    if (localData) {
      setTasks(JSON.parse(localData));
    }
  }, []);


  useEffect(() => {
    localStorage.setItem('myData', JSON.stringify(tasks));
  }, [tasks]);

  const handleInputChange = (e) => {
    setNewTask(e.target.value);
  };



  const addTask = () => {
    if (isEditing) {
      tasks[index] = newTask;
      if (buttonRef.current) {
        buttonRef.current.innerText = 'Add';
      }

      setIsEditing(false);
      setNewTask("");
    } else {
      if (newTask.trim() !== '') {
        setTasks([...tasks, newTask]);
        setNewTask('');
      } else {
        alert("Please enter something.");
      }
    }
  };

  const deleteTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks.splice(index, 1);
    setTasks(updatedTasks);
  };

  const clearAllTasks = () => {
    setTasks([]);
  };

  const toggleTheme = () => {
    setDarkTheme(!darkTheme);
  };

  const updateTasks = (index) => {
    setIsEditing(true);
    setNewTask(tasks[index]);
    if (buttonRef.current) {
      buttonRef.current.innerText = 'Edit';
    }
    setIndex(index);
  };

  const displayTasks = () => {
    return tasks.map((task, index) => (
      <li key={index}>
        {task}
        <button onClick={() => deleteTask(index)}>Delete</button>
        <button onClick={() => updateTasks(index)}>Update</button>
      </li>
    ));
  };

  return (
    <div className={`App ${darkTheme ? 'dark' : 'light'}`}>
      <h1>To-Do List</h1>
      <div className="theme-toggle" onClick={toggleTheme}>
        <i className={`fas fa-${darkTheme ? 'sun' : 'moon'}`}></i>
      </div>
      <div className="input-section">
        <input
          type="text"
          placeholder="Add a new task"
          value={newTask}
          onChange={handleInputChange}
        />
        <button  ref={buttonRef} onClick={addTask}> {isEditing ? 'Edit' : 'Add'}</button>
      </div>
      <ul>
      {displayTasks()}
      </ul>
      <button className="clear-button" onClick={clearAllTasks}>
        Clear All
      </button>
    </div>
  );
};

export default App;
